INSERT INTO daily.daily_menu (name, banner_url, category_id,  price,  discount, description, menu_option) VALUES ('水煮鱼', 'http://ac-ud7op3rB.clouddn.com/JJfgbxDw2FQBPybWJpg0erOUzZm7KQZSa0pxIg6Z?imageView2/1/w/150/h/120', 2,  49.00, 0, '水煮鱼又称江水煮江鱼，是一道四川及重庆市的汉族传统名菜，“麻上头，辣过瘾”，让水煮鱼在全国流行得一塌糊涂', null);
INSERT INTO daily.daily_menu (name, banner_url, category_id,  price,  discount, description, menu_option) VALUES ('回锅肉', 'http://ac-ud7op3rB.clouddn.com/H48HHdspBN2hlkKIA44Aa2y4d9PWfheAc4PSLUBM?imageView2/1/w/150/h/120', 2,  32.00, 0, '回锅肉作为一道传统川菜，一直被认为是川菜之首，川菜的化身。它色香味俱全，色泽红亮，是最好的下饭菜之首选', null);
INSERT INTO daily.daily_menu (name, banner_url, category_id,  price,  discount, description, menu_option) VALUES ('麻婆豆腐', 'http://ac-ud7op3rB.clouddn.com/TLnsrPsI8sbDezRyDtD2Ca6IT47mxDe21F2XG3TI?imageView2/1/w/150/h/120', 2,  32.00, 0, '麻婆豆腐是有名的传统川菜之一，主要由豆腐、碎牛肉/碎猪肉、辣椒、花椒做成，麻来自花椒，辣来自辣椒，这道菜突显了川菜“麻辣”的特点', null);
