INSERT INTO daily.daily_menu (name, banner_url, category_id,  price,  discount, description, menu_option) VALUES ('马卡龙', 'http://ac-ud7op3rB.clouddn.com/kO517WCcMD6CvWEEccuTa0wXbscNGpBiLU9JSGUy?imageView2/1/w/150/h/120', 6,  16.00, 0, '　马卡龙是最具有法国式浪漫色彩的甜点，其名字“少女的酥胸”就已经让人产生无限遐想。', null);
INSERT INTO daily.daily_menu (name, banner_url, category_id,  price,  discount, description, menu_option) VALUES ('欧培拉', 'http://ac-ud7op3rB.clouddn.com/CftbT4vKCAv5rU3lPKK7LXyTk3mFCdO0Jc9en8fJ?imageView2/1/w/150/h/120', 6,  12.00, 0, '欧培拉是款有着数百年历史的蛋糕，根据料理圣经《拉鲁斯美食》里定义的就是：夹加咖啡糖浆和巧克力爽的杏仁奶油蛋糕。', null);
INSERT INTO daily.daily_menu (name, banner_url, category_id,  price,  discount, description, menu_option) VALUES ('舒芙蕾', 'http://ac-ud7op3rB.clouddn.com/GFa1X4J7C0c55H7iEuMGrdhXic0xN9XcZZPFiu84?imageView2/1/w/150/h/120', 6,  12.00, 0, '舒芙蕾，又称蛋奶酥、梳乎厘，是于中世纪诞生的法国著名甜点。法文的“Souffle”就是吹气的意思。它的材料就是鲜奶和蛋白，但采用隔水烘烤，到蛋白膨胀那瞬间。', null);
INSERT INTO daily.daily_menu (name, banner_url, category_id,  price,  discount, description, menu_option) VALUES ('微热山丘台湾土凤梨酥', 'http://ac-ud7op3rB.clouddn.com/DDx1QKOmNJnmQCAh0MQOHdnwQ2svCrYJeYAykVBe?imageView2/1/w/150/h/120', 7,  16.00, 0, '微热山丘凤梨酥号称采用新西兰牛油、日本进口面粉，外皮香酥不会掉屑，内馅是100%纯凤梨制成，所以尝得到丝丝分明的果肉，并且甜中带酸，菠萝香气很浓郁。', null);
INSERT INTO daily.daily_menu (name, banner_url, category_id,  price,  discount, description, menu_option) VALUES ('云南玫瑰鲜花饼', 'http://ac-ud7op3rB.clouddn.com/qYsrZcr9QsOo3BZTGaWnbN5CsnrQz9RbR0XwnuhS?imageView2/1/w/150/h/120', 7,  14.00, 0, '用含苞欲放或者微微开放的玫瑰花的花瓣制作成玫瑰花馅，加之以面粉、香油、白糖、蜂蜜等配料焙烤而制成，酡颜凝新露，化馥沁皮酥。', null);
